import React, { Component } from 'react'
import './App.css'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios'

class Get extends React.Component {
  constructor() {
    super()
    this.state = {
      user: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:8888/student')
      .then(response => {
        console.log(response.data)
        this.setState({ user: response.data });
        console.log(this.state.user);
      })
  }

  render() {
    return (

      <div style={text_center}>
        <table style={width1}>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Age</th>
          </tr>

          {this.state.user.map(users =>
            <tr>
              <td key={users.id}>{users.id}</td>
              <td key={users.id}>{users.name}</td>
              <td key={users.id}>{users.age}</td>
            </tr>
          )}
        </table>
      </div>

    )
  }
}

class Delete extends Component {
  constructor(){
    super()
    this.state={
      id: 0
    }
  }

  handleClick(e){
    e.preventDefault();
    if(this.state.id !== undefined){
      console.log(this.state.id)
    }
  }

  getID(e) {
    console.log(e.target.value)
    this.setState({ id: e.target.value });
  }

  render() {
    return (
      <div>
        <div style={text_center} onSubmit={this.handleClick}>
          <input onChange={this.getID} type="text" placeholder="ID"></input>
          <button>Delete</button>
        </div>
      </div>
    )
  }
}

const width = {
  width: '100px',
  marginLeft: '10px'
}
const width1 = {
  marginLeft: '10%', width: '80%', textAlign: 'center'
}
const text_center = {
  textAlign: 'center'
}
const margin = {
  margin: '2% 0'
}
function Show() {
  return (
    <Router>
      <div>
        <div style={Object.assign(margin, text_center)}>
          <Link to="/get"><button id="get" style={width}>GET</button></Link>
          <Link to="/post"><button id="post" style={width}>POST</button></Link>
          <Link to="/put"><button id="put" style={width}>PUT</button></Link>
          <Link to="/delete"><button id="delete" style={width}>DELETE</button></Link>
        </div>

        <Route path="/get" component={Get1} />
        {/* <Route path="/post" component={Post} /> */}
        {/* <Route path="/put" component={Put} /> */}
        <Route path="/delete" component={Delete1} />
      </div>
    </Router>
  );


}

function Get1() {
  return (
    <div>
      <Get />
    </div>
  );
}
// function Post() {
