//     <div>
//       {/* <Get /> */}
//     </div>
//   );
// }
// function Put() {
//   return(
//     <div>
//       {/* <Get /> */}
//     </div>
//   );
// }
function Delete1() {
  return (
    <div>
      <Delete />
    </div>
  );
}

export default Show;
