// }
const text_center = {
  textAlign: 'center'
}
const margin = {
  margin: '2% 0'
}
function Show() {
  return (
    <Router>
      <div>
        <div style={Object.assign(margin, text_center)}>
          <Link to="/get"><button id="get" style={width} className="btn btn-info">GET</button></Link>
          <Link to="/post"><button id="post" style={width} className="btn btn-info {this.state.display}">POST</button></Link>
          <Link to="/put"><button id="put" style={width} className="btn btn-info">PUT</button></Link>
          <Link to="/delete"><button id="delete" style={width} className="btn btn-info">DELETE</button></Link>
        </div>

        <Route path="/get" component={Get1} />
        {/* <Route path="/post" component={Post} /> */}
        {/* <Route path="/put" component={Put} />
          <Route path="/delete" component={Delete} />  */}
      </div>
    </Router>
  );


}

function Get1() {
  return (
    <div>
