import React, { Component } from 'react'
import './App.css'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios'
import {button} from 'react-bootstrap'

class Get extends React.Component {
  constructor() {
    super()
    this.state = {
      user: []
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    axios.get('http://localhost:8888/student').then(response => {
      this.setState({
        user: response.data
      })
    })
  }

  render() {
    return (
      <div className='button__container'>
        <button className='button' onClick={this.handleClick}>Click Me</button>
        {this.state.user.map(users =>
          <div className='container disable' style={text_center}>
            <table className='table' style={width1}>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Age</th>
              </tr>
              <tr>
                <td key={users.id}>{users.id}</td>
                <td key={users.id}>{users.name}</td>
                <td key={users.id}>{users.age}</td>
              </tr>
            </table>
          </div>
        )}


      </div>
    )
  }
}
const width = {
  width: '100px',
  marginLeft: '10px',
}
const width1 = {
  marginLeft: '10%', width: '80%', textAlign: 'center'
}
const text_center = {
  textAlign: 'center'
}
const margin = {
  margin: '2% 0'
}
function Show() {
  return (
    <Router>
      <div>
        <div style={Object.assign(margin, text_center)}>
          <Link to="/get"><button id="get" style={width} className="btn btn-info">GET</button></Link>
          <Link to="/post"><button id="post" style={width} className="btn btn-info {this.state.display}">POST</button></Link>
          <Link to="/put"><button id="put" style={width} className="btn btn-info">PUT</button></Link>
          <Link to="/delete"><button id="delete" style={width} className="btn btn-info">DELETE</button></Link>
        </div>

        <Route path="/get" component={Get1} />
        {/* <Route path="/post" component={Post} /> */}
        {/* <Route path="/put" component={Put} />
          <Route path="/delete" component={Delete} />  */}
      </div>
    </Router>
  );


}

function Get1() {
  return (
    <div>
      <Get />
    </div>
  );
}
