import React, { Component } from 'react';
import './App.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  renderSquare(i) {
    return <Square value={this.state.squares[i]} onClick={() => this.handleClick(i)} />;
  }

  renderAllSquares() {
    const matrixSize = Math.sqrt(this.props.squares.length);
    const board = Array(matrixSize).fill(null);
    for (let i = 0; i < matrixSize; i++) {
      const squares = Array(matrixSize).fill(null);
      for (let j = 0; j < matrixSize; j++) {
        var squareKey = i * matrixSize + j;
        squares.push(<span key={squareKey}>{this.renderSquare(squareKey)}</span>);
      }
      board.push(<div key={i}>{squares}</div>);
    }
    return board;
  }

  render() {
    return (
      <div>
        <div>{this.renderAllSquares()}</div>

      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [
        {
          squares: Array(1600).fill(null)
        }
      ],
      stepNumber: 0,
      xIsNext: true,
      winner: null
    };

  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = this.state.winner;
    if (this.state.winner || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? "X" : "O";
    this.setState({
      history: history.concat([
        {
          squares: squares
        }
      ]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext
    });
    if(calculateWinner(i, squares[i]. square)){
      this.setState({
        winner: squares[i],
      })
    }
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0
    });
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);

    const moves = history.map((step, move) => {
      const desc = move ?
        'Go to move #' + move :
        'Go to game start';
      return (
        <li key={move}>
          <button onClick={() => this.jumpTo(move)}>{desc}</button>
        </li>
      );
    });
    let i = -1;
    let status;
    if (winner) {
      status = "Winner is: " + winner; // Nếu winner có giá trị thì sẽ hiển thị người thắng cuộc
    } else {
      status = "Next player is: " + (this.state.xIsNext ? 'X' : 'O');
    }


    const row = Array(20).fill(null);
    const col = Array(30).fill(null);
    return (
      <div>
        <div className="status">{status}</div>
        {
          row.map(e => {
            return (<div className="board-row">{
              col.map(v => { i++; return this.renderSquare(i) })
            }</div>)
          })
        }
      </div>
    );
  }
}

function calculateWinner(i,square,squares) {
  console.log(square);
  for (let m = 1; m <= 5; m++) { //duyet hang ngang
    for (let n = i - 5 + m; n <= i - 1 + m; n++) {
      if (squares[n] !== square)
        break;
      if (n === (i - 1 + m)) {
        return square;
      }
    }
  }
  for (let m = 1; m <= 5; m++) { //duyet hang doc
    for (let n = i - (30 * (5 - m)); n <= i - 30 + (30 * m); n += 30) {
      if (squares[n] !== square)
        break;
      if (n === (i - 30 + (m * 30))) {
        return square;
      }
    }
  }
  for (let m = 1; m <= 5; m++) { //hang cheo 1
    for (let n = i - (31 * (5 - m)); n <= i - 31 + (31 * m); n += 31) {
      if (squares[n] !== square)
        break;
      if (n === (i - 31 + (m * 31))) {
        return square;
      }
    }
  }
  for (let m = 1; m <= 5; m++) { //hang cheo 2
    for (let n = i - (29 * (5 - m)); n <= i - 29 + (29 * m); n += 29) {
      if (squares[n] !== square)
        break;
      if (n === (i - 29 + (m * 29))) {
        return square;
      }
    }
  }
  return null;

}


export default Game;
