import React, { Component } from 'react';
import './App.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      square: Array(9).fill(null),
      isNext: true,
    };
  }

  handleClick(i) {
    const squares = this.state.square.slice();
    squares[i] = this.state.isNext ? 'X' : 'O';
    console.log(this.state.isNext);
    this.setState({
      square: squares,
      xIsNext: !this.state.isNext,
    });
  }


  renderSquare(i){
    return( 
      <Square 
        value={this.state.square[i]} 
        onClick={() => this.handleClick(i)}
        />
    );
  }

  render(){
    const status = 'Next player: ' + (this.state.isNext ? 'X' : 'O');

    return(
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component{
  render(){
    return(
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div></div>
          <ol></ol>
        </div>
      </div>
    );
  }
}


export default Game;
