import React, { Component } from 'react';
import './App.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}



class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      square: Array(9).fill(null),
      isNext: true,
    };
  }



  handleClick(i) {
    const squares = this.state.square.slice();
    squares[i] = this.state.isNext ? 'X' : 'O';
    console.log(this.state.isNext);
    this.setState({
      square: squares,
      isNext: !this.state.isNext,
    });
  }


  renderSquare(i){
    return( 
      <Square 
        value={this.state.square[i]} 
        onClick={() => this.handleClick(i)}
        />
    );
  }

  render(){
    const winner = calcatulatedWinner(this.state.squares);
    let status;
    if(winner){
      status = 'Winner: '+status;
    } else {
      status = 'Next Player: ' + (this.state.isNext ? 'X' : 'O');
    }

    return(
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component{
  render(){
    return(
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div></div>
          <ol></ol>
        </div>
      </div>
    );
  }
}

function calcatulatedWinner(squares){
  const lines = [ 
    [0,1,2],
    [0,3,6],
    [0,4,8],
    [1,4,7],
    [2,5,8],
    [2,4,6],
    [3,4,5],
    [6,7,8],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a,b,c]= lines;
    if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
      return squares[a];
    }

  }
  return null;
}

export default Game;
